sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var workParent = new GlideRecord ('x_0890_devops_work');
	var workRef = current.work;
	workParent.addQuery('sys_id',workRef);
	workParent.query();
	gs.info("JCD Work Ref: "+workRef.number);
	while (workParent.next()){
		workParent.development_objects_added = true;
		workParent.work_notes = 'Objects added by '+gs.getUserDisplayName();+"\nSee Objects tab for more information";
		workParent.update();
	}

})(current, previous);
