sys_script_client.script:
function onLoad() {

	changeButtonColor('create_change', '#1f256b');
	changeButtonColor('save', '#11631c');	
	changeButtonColor('proceed', '#11631c');
	changeButtonColor('submit_peer','#11631c');
	changeButtonColor('submit_qa','#11631c');

	if (g_form.getValue('stage')=='Awaiting Assignment'){
		changeButtonColor('atm', '#11631c');
	}
	if (g_form.getValue('stage')!='Awaiting Assignment'){
		changeButtonColor('atm', '#add1a1');
	}



	function changeButtonColor(buttonID, backgroundColor) {
		try{
			//Find the button(s) by ID and change the background color
			$$('button[id=' + buttonID + ']').each(function(elmt) {
				elmt.style.backgroundColor = backgroundColor;
				elmt.style.color = '#ffffff'; //make the button text white
			});
		}catch(e){}
	}


}
