sys_ui_action.script:
function generateQA(){
	var answer=confirm("Are you sure you wish to submit this development for Quality Assurance Review?");
	if (answer==true)
	{
		gsftSubmit(null, g_form.getFormElement(), 'submit_qa'); //MUST call the 'Action name' set in this UI Action
	}
	else
	{
		return false;
	}
}

if(typeof window == 'undefined')
{
	var gr = new GlideRecord('x_0890_devops_quality_assurance_tasks');
	gr.initialize();
	gr.newRecord();
	gr.short_description = 'Quality Assurance task for '+current.number;
	gr.parent = current.sys_id;
	gr.priority = current.priority;
	gr.assignment_group = 'b6a58111db8707001e5836ee9d9619e5'; //QA Group sys_id
	gr.description = 'Quality Assurance Task for '+current.number+'\n\nDevelopment Description:\n'+current.description;

	var newID = gr.insert();

	var gdt = new GlideDateTime().getDisplayValue();
	current.work_notes = 'Submitted for Quality Assurance on '+gdt+' by '+gs.getUserDisplayName();
	current.qa_start_date = gdt;
	current.stage = 'QA';
	current.update();

	gs.addInfoMessage(gs.getMessage("Quality Assurance Task {0} has been created and assigned to the QA team.", gr.number));

	action.setRedirectURL(current);
	action.setReturnURL(current);
}
