sys_ui_action.script:
function generatePass(){
	var answer=confirm("Click OK if all Quality Assurance tests have passed.");
	if (answer==true)
	{
		gsftSubmit(null, g_form.getFormElement(), 'qa_pass'); //MUST call the 'Action name' set in this UI Action
	}
	else
	{
		return false;
	}
}

if(typeof window == 'undefined')
{
	var gr = new GlideRecord('x_0890_devops_work');
	gr.addQuery('sys_id',current.parent);
	gr.query();
	while (gr.next()){
		var gdt = new GlideDateTime().getDisplayValue();
		gr.work_notes = 'Quality Assurance Passed at '+gdt+' by '+gs.getUserDisplayName();
		gr.journal_2 = 'Quality Assurance Passed at '+gdt+' by '+gs.getUserDisplayName();
		gr.stage = 'Ready to Promote';
		gr.update();
		gs.addInfoMessage(gs.getMessage("Work Record {0} has been updated as QA PASS.", gr.number));
	}

	var gdt2 = new GlideDateTime().getDisplayValue();
	current.work_notes = 'Quality Assurance Passed at '+gdt2+' by '+gs.getUserDisplayName();
	current.state = 90;
	current.active = 'false';
	current.update();

	action.setRedirectURL(current);
	action.setReturnURL(current);
}
